CREATE OR REPLACE FUNCTION html_atualiza_item_prescr ( nr_prescricao_p prescr_material.nr_prescricao%type, nr_seq_cpoe_p cpoe_material.nr_sequencia%type, cd_material_p prescr_material.cd_material%type, cd_unidade_medida_dose_p prescr_material.cd_unidade_medida_dose%type, qt_dose_p prescr_material.qt_dose%type, ds_dose_diferenciada_p prescr_material.ds_dose_diferenciada%type, qt_solucao_p prescr_material.qt_solucao%type, ie_agrupador_p prescr_material.ie_agrupador%type) RETURNS varchar AS $body$
DECLARE


cd_material_w				prescr_material.cd_material%type;
cd_unidade_medida_dose_w	prescr_material.cd_unidade_medida_dose%type;
qt_dose_w					prescr_material.qt_dose%type;
ds_dose_diferenciada_w		prescr_material.ds_dose_diferenciada%type;
qt_solucao_w				prescr_material.qt_solucao%type;
ie_retorno_w				varchar(2);

BEGIN

select	max(cd_material),
		max(cd_unidade_medida_dose),
		max(qt_dose),
		max(ds_dose_diferenciada),
		max(qt_solucao)
into STRICT	cd_material_w,
        cd_unidade_medida_dose_w,
        qt_dose_w,
        ds_dose_diferenciada_w,
        qt_solucao_w
from	prescr_material
where	nr_prescricao = nr_prescricao_p
and		nr_seq_mat_cpoe = nr_seq_cpoe_p
and		coalesce(ie_suspenso,'N') = 'N'
and		ie_agrupador = ie_agrupador_p;

ie_retorno_w := 'N';
if (cd_material_w <> cd_material_p) or (cd_unidade_medida_dose_w <> cd_unidade_medida_dose_p) or (qt_dose_w <> qt_dose_p) or (ds_dose_diferenciada_w <> ds_dose_diferenciada_p) or (coalesce(qt_solucao_w,0) <> coalesce(qt_solucao_p,0)) then
	ie_retorno_w := 'S';
end if;

return ie_retorno_w;

end;
$body$
