CREATE OR REPLACE FUNCTION html_obter_compatibilidade_rel (nr_seq_rel_philips_p bigint, nr_seq_rel_cliente_p bigint) RETURNS bigint AS $body$
DECLARE


qt_param_cliente_w      bigint;
qt_param_philips_w      bigint;
qt_param_compativel_w   bigint;
retorno_w               double precision;


BEGIN

retorno_w := 0;

select  count(*)
into STRICT    qt_param_cliente_w
from    relatorio_parametro
where   nr_seq_relatorio = nr_seq_rel_cliente_p;

select  count(*)
into STRICT    qt_param_philips_w
from    relatorio_parametro
where   nr_seq_relatorio = nr_seq_rel_philips_p;

if (qt_param_philips_w > 0) and (qt_param_cliente_w > 0) then

        select  count(*)
        into STRICT    qt_param_compativel_w
        from    relatorio_parametro a
        where   a.nr_seq_relatorio = nr_seq_rel_cliente_p
        and     exists (SELECT 1
                from    relatorio_parametro x
                where   x.nr_seq_relatorio      = nr_seq_rel_philips_p
                and     upper(x.cd_parametro)   = upper(a.cd_parametro));

        retorno_w       := dividir(qt_param_compativel_w,qt_param_philips_w) * 100;

end if;

return  retorno_w;

end;
