CREATE OR REPLACE FUNCTION html_obter_legenda_oc ( nr_ordem_compra_p bigint) RETURNS varchar AS $body$
DECLARE


/*
Function utilizada para retornar a legenda correta
a ser apresentada para a Ordem de Compra

Cod. Legenda: 1921
Utilizacao: Ordem Compra
Tab: Ordem Compra

Resultados:
X : Nao se aplica / Retorno padrao
N : Nao liberada
P : Parcialmente recebida
A : Atrasada
B : Baixada
AP: Aprovada
C : Cancelada
*/
ie_retorno_w		varchar(2) := 'X';
dt_liberacao_w		ordem_compra.dt_liberacao%type;
dt_baixa_w			ordem_compra.dt_baixa%type;
dt_entrega_w		ordem_compra.dt_entrega%type;
qt_existe_nf_w		smallint;
dt_aprovacao_w      ordem_compra.dt_aprovacao%type;
nr_seq_motivo_cancel_w  ordem_compra.nr_seq_motivo_cancel%type;


BEGIN

select	dt_liberacao,
	dt_baixa,
    dt_entrega,
    dt_aprovacao,
    nr_seq_motivo_cancel
into STRICT    dt_liberacao_w,
    dt_baixa_w,
    dt_entrega_w,
    dt_aprovacao_w,
    nr_seq_motivo_cancel_w
from    ordem_compra
where    nr_ordem_compra = nr_ordem_compra_p;

select    max(1)
into STRICT    qt_existe_nf_w
from    ordem_compra_nota_fiscal
where    nr_ordem_compra = nr_ordem_compra_p;

if (nr_seq_motivo_cancel_w IS NOT NULL AND nr_seq_motivo_cancel_w::text <> '') then
    ie_retorno_w    := 'C';
elsif (coalesce(dt_liberacao_w::text, '') = '') then
    ie_retorno_w     := 'N';

elsif (qt_existe_nf_w > 0) and (coalesce(dt_baixa_w::text, '') = '') then
    ie_retorno_w    := 'P';

elsif (coalesce(dt_baixa_w::text, '') = '') and (trunc(dt_entrega_w) < trunc(clock_timestamp())) then
    ie_retorno_w    := 'A';

elsif (dt_baixa_w IS NOT NULL AND dt_baixa_w::text <> '') then 
    ie_retorno_w    := 'B';

elsif (dt_aprovacao_w IS NOT NULL AND dt_aprovacao_w::text <> '') then
    ie_retorno_w    := 'AP';
end if;

return    ie_retorno_w;
exception
when no_data_found then return null;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION html_obter_legenda_oc ( nr_ordem_compra_p bigint) FROM PUBLIC;

