SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION html_to_str_grid ( ds_texto_p text) RETURNS varchar AS $body$
DECLARE

						
ds_texto_w varchar(32000);
x 			varchar(32000) := null;
in_html boolean  := FALSE;
s varchar(1);

BEGIN

	
if (ds_texto_p IS NOT NULL AND ds_texto_p::text <> '') and
   (( position('<html' in ds_texto_p) > 0) or ( position('<b' in ds_texto_p) > 0) or (position('</b' in ds_texto_p) > 0) or (position('</center' in ds_texto_p) > 0 or (position('<br' in ds_texto_p) > 0))) then
	
	for i in 1 .. length( ds_texto_p ) loop
		s := substr( ds_texto_p, i, 1 );
		
		if in_html then
			if s = '>' then
				in_html := FALSE;
				x := ltrim(x||' ');
			end if;
		else
			if s = '<' then
				in_html := TRUE;
			end if;
		end if;
		
		if not in_html and s != '>' then
			x := substr(x || s,1,32000 );
		end if;
	end loop;
	
	return x;
end if;	

return ds_texto_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
