CREATE OR REPLACE FUNCTION ibcm_obter_codigo_item ( ie_tipo_atendimento_p bigint, nr_interno_conta_p bigint, cd_item_p bigint, ie_origem_proced_p bigint, ie_tipo_item_p bigint) RETURNS varchar AS $body$
DECLARE

	 
cd_codigo_w		varchar(12);
cd_tipo_procedimento_w	smallint;
cd_convenio_w		integer;
cd_convenio_glosa_w	integer;


BEGIN 
 
select	max(c.cd_convenio), 
	max(c.cd_convenio_glosa) 
into STRICT	cd_convenio_w, 
	cd_convenio_glosa_w 
from	conta_paciente a, 
	atendimento_paciente b, 
	atend_categoria_convenio c 
where	a.nr_interno_conta 	= nr_interno_conta_p 
and	a.nr_atendimento	= b.nr_atendimento 
and	b.nr_atendimento	= c.nr_atendimento;
 
if (ie_tipo_item_p = 1) then 
 
	cd_codigo_w	:= cd_item_p;
	 
	select	max(cd_tipo_procedimento) 
	into STRICT	cd_tipo_procedimento_w 
	from	procedimento 
	where	cd_procedimento 	= cd_item_p 
	and	ie_origem_proced	= ie_origem_proced_p 
	and	coalesce(ie_situacao, 'I')	= 'A';
 
	if (ie_tipo_atendimento_p = 3) and (cd_tipo_procedimento_w = 71) then 
		 
		if (cd_convenio_w = 2) and (cd_convenio_glosa_w = 12) then 
			 
			cd_codigo_w	:= '03';
			 
		elsif (cd_convenio_w = 12) then 
		 
			cd_codigo_w	:= '27';
			 
		end if;
	 
	end if;
 
else 
 
	if (ie_tipo_atendimento_p = 3) then 
		 
		if (cd_convenio_w = 2) and (cd_convenio_glosa_w = 12) then 
			 
			cd_codigo_w	:= '04';
			 
		elsif (cd_convenio_w = 12) then 
		 
			cd_codigo_w	:= '61';
			 
		end if;
 
	elsif (ie_tipo_atendimento_p = 7) then 
	 
		if (cd_convenio_w = 2) and (cd_convenio_glosa_w = 12) then 
			 
			cd_codigo_w	:= '09';
			 
		elsif (cd_convenio_w = 12) then 
		 
			cd_codigo_w	:= '135';
			 
		end if;
	 
	end if;	
 
end if;
 
return	cd_codigo_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
