CREATE OR REPLACE FUNCTION identify_type_convenant_nom_mx (cd_pessoa_fisica_p pessoa_fisica.cd_pessoa_fisica%type, nr_atendimento_p convenio.cd_convenio%type, dt_referencia_p timestamp ) RETURNS varchar AS $body$
DECLARE


ds_retorno_w			varchar(1) := 'N';

nr_seq_nom_log_w		nom_log_convenio_mx.nr_sequencia%type;
nr_seq_nom_log_pes_w		nom_log_convenio_mx.nr_sequencia%type;
ie_tipo_envio_w			nom_log_convenio_mx.ie_tipo_envio%type;
dt_envio_w			nom_log_convenio_mx.dt_envio%type;

dt_inicio_w			timestamp;
dt_final_w			timestamp;

cd_convenio_w			atend_categoria_convenio.cd_convenio%type;


BEGIN

dt_inicio_w	:= pkg_date_utils.start_of(dt_referencia_p,'MONTH');
dt_final_w	:= pkg_date_utils.end_of(dt_referencia_p,'MONTH');


select	min(b.cd_convenio)
into STRICT	cd_convenio_w
from	atendimento_paciente a,
	atend_categoria_convenio b
where 	a.nr_atendimento	= nr_atendimento_p
and	a.nr_atendimento	= b.nr_atendimento;

select 	max(nr_sequencia)
into STRICT	nr_seq_nom_log_w
from	nom_log_convenio_mx
where	cd_pessoa_fisica	= cd_pessoa_fisica_p
and	cd_convenio		= cd_convenio_w;

select 	max(nr_sequencia)
into STRICT	nr_seq_nom_log_pes_w
from	nom_log_convenio_mx
where	cd_pessoa_fisica	= cd_pessoa_fisica_p;

if (nr_seq_nom_log_w IS NOT NULL AND nr_seq_nom_log_w::text <> '') then

	select	ie_tipo_envio
	into STRICT	ie_tipo_envio_w
	from	nom_log_convenio_mx
	where	nr_sequencia	= nr_seq_nom_log_w
	and	dt_envio	between dt_inicio_w and dt_final_w;

	if (ie_tipo_envio_w IS NOT NULL AND ie_tipo_envio_w::text <> '') then

		ds_retorno_w := ie_tipo_envio_w;
	else
		ds_retorno_w := 'A';
	end if;
elsif (nr_seq_nom_log_pes_w IS NOT NULL AND nr_seq_nom_log_pes_w::text <> '') and (coalesce(nr_seq_nom_log_w::text, '') = '') then

	ds_retorno_w := 'A';
else
	ds_retorno_w := 'N';

end if;


return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
