CREATE OR REPLACE FUNCTION ie_cadastro_pacientes_pessoa (cd_pessoa_fisica_p text) RETURNS varchar AS $body$
DECLARE

qt_retorno_w	real;

BEGIN

if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') then

  select  count(*)
  into STRICT	qt_retorno_w
	from    HD_EQUIPE_PACIENTE d
	where   d.cd_pessoa_fisica  = cd_pessoa_fisica_p;

  if (qt_retorno_w = 0)then
    select count(*)
    into STRICT	qt_retorno_w
    from   HD_ACESSO  a
    where  a.cd_pessoa_fisica  = cd_pessoa_fisica_p;
  end if;

  if (qt_retorno_w = 0)then
    select count(*)
    into STRICT	qt_retorno_w
    from   HD_ESCALA_DIALISE  b
    where  b.cd_pessoa_fisica  = cd_pessoa_fisica_p;
  end if;

  if (qt_retorno_w = 0)then
    select count(*)
    into STRICT	qt_retorno_w
    from   HD_PROFISSIONAL_RESP  c
    where  c.cd_pessoa_fisica  = cd_pessoa_fisica_p;
  end if;

end if;

return	qt_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
