CREATE OR REPLACE FUNCTION ie_prescricoes_adep_hor ( nr_prescricao_p text, nr_prescricoes_p text, cd_setor_atendimento_p text, nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE



ie_retorno_w			varchar(1) := 'N';



BEGIN
	if (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') then
		EXECUTE 'select 	nvl(max(''S''), ''N'')
		from 	prescr_medica a
		where	a.CD_SETOR_ATENDIMENTO in (' || cd_setor_atendimento_p || ')
		and 	a.nr_atendimento = ' || nr_atendimento_p || '
		and		a.nr_prescricao = ' || nr_prescricao_p || ''
        into STRICT ie_retorno_w;
	end if;

	if (ie_retorno_w = 'N' and (nr_prescricoes_p IS NOT NULL AND nr_prescricoes_p::text <> '')) then
		EXECUTE 'select 	nvl(max(''S''), ''N'')
		from 	(	select 		regexp_substr(:nr_prescricoes_p, ''[^,]+'', 1, level) txt from dual
					connect by 	regexp_substr(:nr_prescricoes_p, ''[^,]+'', 1, level) is not null
				) a, prescr_medica b
		where	b.nr_prescricao = a.txt 
		and 	b.CD_SETOR_ATENDIMENTO in ('|| cd_setor_atendimento_p || ') 
		and 	b.nr_atendimento = ' || nr_atendimento_p ||''
        into STRICT ie_retorno_w
        using nr_prescricoes_p, nr_prescricoes_p;

	end if;

return  ie_retorno_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
