CREATE OR REPLACE FUNCTION ingoh_compara_abo_rh ( nr_seq_transfusao_p bigint) RETURNS varchar AS $body$
DECLARE

 
ie_fator_rh_w		varchar(1);			
ie_tipo_sangue_w	varchar(2);			
ie_fator_rh_prod_w	varchar(1);
ie_tipo_sangue_prod_w	varchar(2);
ds_retorno_w		varchar(1);
C01 CURSOR FOR
	SELECT	ie_fator_rh, 
		ie_tipo_sangue 
	from	san_producao_v 
	where	nr_seq_transfusao = nr_seq_transfusao_p;	

BEGIN 
ds_retorno_w	:= 'N';
 
if (nr_seq_transfusao_p IS NOT NULL AND nr_seq_transfusao_p::text <> '') then 
	select	a.ie_fator_rh, 
		a.ie_tipo_sangue 
	into STRICT	ie_fator_rh_w, 
		ie_tipo_sangue_w 
	from	pessoa_fisica a, 
		san_transfusao b 
	where	a.cd_pessoa_fisica = b.cd_pessoa_fisica 
	and	b.nr_sequencia = nr_seq_transfusao_p;	
		 
	open C01;
	loop 
	fetch C01 into	 
		ie_fator_rh_prod_w, 
		ie_tipo_sangue_prod_w;
	EXIT WHEN NOT FOUND or ds_retorno_w = 'S';  /* apply on C01 */
		begin 
		if	((ie_fator_rh_w <> ie_fator_rh_prod_w) or (coalesce(ie_fator_rh_prod_w::text, '') = '')) then 
			ds_retorno_w	:= 'S';
		elsif	((ie_tipo_sangue_w <> ie_tipo_sangue_prod_w) or (coalesce(ie_tipo_sangue_prod_w::text, '') = '')) then 
			ds_retorno_w	:= 'S';
		end if;	
		end;
	end loop;
	close C01;
end if;	
 
return	ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
