CREATE OR REPLACE FUNCTION insere_barra_entre_campos (ds_valor_campo_p text, ds_caracter_p text, nr_campos_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(255);
ds_caracter_w		varchar(255);
i			integer	:= 1;
j			integer	:= 1;
nr_max_campos_w		bigint := 0;


BEGIN

ds_retorno_w := ds_caracter_p;
ds_caracter_w	:= ds_caracter_p;

if ((length(ds_valor_campo_p) IS NOT NULL AND (length(ds_valor_campo_p))::text <> '')) then
	nr_max_campos_w := length(ds_valor_campo_p);
end if;

for	i in 1 .. nr_max_campos_w loop
	ds_retorno_w	:= ds_retorno_w ||' '|| substr(ds_valor_campo_p,i,1) || ' ' || ds_caracter_w;
end loop;
if (nr_max_campos_w < nr_campos_p) then
	for	j in 1 .. (nr_campos_p - nr_max_campos_w) loop
		ds_retorno_w	:= ds_retorno_w || '    ' || ds_caracter_w;
	end loop;
end if;

return ds_retorno_w;

end;
