CREATE OR REPLACE FUNCTION insere_espaco_desc_local ( ds_valor_campo_p text, nr_caracter_p bigint) RETURNS varchar AS $body$
DECLARE


ds_texto_w	varchar(2000);
ds_retorno_w	varchar(2000) := '';
ds_retorno1_w	varchar(2000) := '';
i		bigint;
cont		bigint;


BEGIN

i		:=1;
cont 		:= 1;
ds_texto_w 	:= ds_valor_campo_p;

while(i <= length(ds_texto_w)) loop
	if (substr(ds_texto_w,i,1) = '/') then
		ds_retorno_w := ds_retorno_w || ' ' || substr(ds_texto_w,i,1) || ' ';
	else
		ds_retorno_w := ds_retorno_w || substr(ds_texto_w,i,1);
	end if;
	i := i + 1;

end loop;
i := 1;
while(i <= length(ds_retorno_w)) loop
	if 	((i mod nr_caracter_p) = 0) and (substr(ds_retorno_w,i,1) <> ' ') and (substr(ds_retorno_w,i,1) <> '/') and (substr(ds_retorno1_w,length(ds_retorno1_w),1) <> ' ') then
		ds_retorno1_w := ds_retorno1_w || substr(ds_retorno_w,i,1) || ' ';
	else
		ds_retorno1_w := ds_retorno1_w || substr(ds_retorno_w,i,1);
	end if;
	i := i + 1;
end loop;

return	ds_retorno1_w;

end;
