CREATE OR REPLACE FUNCTION intervalo_presc (nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE


intervalo_desc_w  intervalo_prescricao.ds_intervalo%TYPE;
ie_refeicao_w  atend_previsao_alta.ie_refeicao%TYPE;


BEGIN
    begin
        select TO_CHAR(max(ie_refeicao))
        into STRICT ie_refeicao_w
        from atend_previsao_alta
        where nr_atendimento = nr_atendimento_p;

        SELECT TO_CHAR(max(ds_intervalo))
        into STRICT intervalo_desc_w
        FROM intervalo_prescricao
        WHERE ie_operacao = 'F'
        AND REGEXP_LIKE(ds_horarios ,'^([0-1]?[0-9]|2[0-3]):[0-5][0-9]$')
        and ds_horarios = ie_refeicao_w;
    exception
        when data_exception then 
            intervalo_desc_w := null;
        when no_data_found then
            intervalo_desc_w := null;
        when data_exception then
            intervalo_desc_w := null;
    end;
return intervalo_desc_w;

end;
$body$
