CREATE OR REPLACE FUNCTION intpd_obter_classif_evento (NR_SEQ_EVENTO_SISTEMA_P bigint) RETURNS varchar AS $body$
DECLARE


ds_classificacao_w	CLASSIFICACAO_INTEGRACAO.DS_CLASSIFICACAO%type;



BEGIN

if (NR_SEQ_EVENTO_SISTEMA_P IS NOT NULL AND NR_SEQ_EVENTO_SISTEMA_P::text <> '') then
	begin

	select	max(substr(a.DS_CLASSIFICACAO,1,length(a.DS_CLASSIFICACAO)))
	into STRICT	ds_classificacao_w
	from	CLASSIFICACAO_INTEGRACAO a,
			INTPD_EVENTOS b,
			INTPD_EVENTOS_SISTEMA c,
			intpd_fila_transmissao d
	where	a.nr_sequencia = b.nr_seq_classif
	and		b.nr_sequencia = c.nr_seq_evento
	and		c.nr_sequencia = d.nr_seq_evento_sistema
	and		d.nr_seq_evento_sistema = NR_SEQ_EVENTO_SISTEMA_P;

	end;
end if;

return	ds_classificacao_w;

end;
