CREATE OR REPLACE FUNCTION intpd_obter_se_regra_log ( nr_seq_evento_int_p bigint) RETURNS varchar AS $body$
DECLARE


ie_regra_w	varchar(1);


BEGIN
begin
select	'S'
into STRICT	ie_regra_w
from	intpd_eventos a,
	intpd_config_eventos b,
	intpd_config_evento_tab c
where	a.ie_evento = b.ie_evento
and	b.nr_sequencia = c.nr_seq_evento
and	a.nr_sequencia = nr_seq_evento_int_p  LIMIT 1;

if (obter_versao_oracle < 11) then
	ie_regra_w	:=	'N';
end if;
exception
when others then
	ie_regra_w	:=	'N';
end;

return	ie_regra_w;

end;
$body$
