CREATE OR REPLACE FUNCTION ipasgo_obter_se_respcred_soma ( ie_responsavel_credito_p procedimento_paciente.ie_responsavel_credito%type) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(15) 	:= 'N';
qt_resp_soma_w		bigint	:= 0;


BEGIN

select	count(1)
into STRICT	qt_resp_soma_w
from	regra_honorario
where	cd_regra = ie_responsavel_credito_p
and	ie_repassa_medico = 'N'
and	ie_entra_conta <> 'S'  LIMIT 1;

if (qt_resp_soma_w > 0) then
	ds_retorno_w := 'S';
end if;

return	coalesce(ds_retorno_w,'N');

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
