CREATE OR REPLACE FUNCTION ipe_obter_se_agrup_mat (cd_estabelecimento_p bigint, nr_seq_matpaci_p bigint) RETURNS varchar AS $body$
DECLARE


/* lhalves OS 824645 em 10/03/2015
Function utilizada no c02 da GERAR_INTERF_CONTA_ITEM_IPE
Esta regra serve para indicar se o item deve ou não ser agrupado no código 32200005
*/
qt_regra_w		bigint;
dt_material_w		timestamp;
cd_grupo_material_w	regra_agrup_mat_convenio.cd_grupo_material%type;
cd_subgrupo_material_w	regra_agrup_mat_convenio.cd_subgrupo_material%type;
cd_classe_material_w	regra_agrup_mat_convenio.cd_classe_material%type;
cd_material_w		regra_agrup_mat_convenio.cd_material%type;
ie_agrupar_w		regra_agrup_mat_convenio.ie_agrupar%type;


c01 CURSOR FOR
SELECT	ie_agrupar
from	regra_agrup_mat_convenio
where	coalesce(cd_estabelecimento,coalesce(cd_estabelecimento_p,0))	= coalesce(cd_estabelecimento_p,0)
and	coalesce(cd_grupo_material,coalesce(cd_grupo_material_w,0))	= coalesce(cd_grupo_material_w,0)
and	coalesce(cd_subgrupo_material,coalesce(cd_subgrupo_material_w,0))	= coalesce(cd_subgrupo_material_w,0)
and	coalesce(cd_classe_material,coalesce(cd_classe_material_w,0))	= coalesce(cd_classe_material_w,0)
and	coalesce(cd_material,coalesce(cd_material_w,0))			= coalesce(cd_material_w,0)
and	dt_material_w	between dt_inicio_vigencia and 		coalesce(dt_fim_vigencia,clock_timestamp() + interval '1 days')
order by dt_inicio_vigencia,
	coalesce(cd_material,0),
	coalesce(cd_classe_material,0),
	coalesce(cd_subgrupo_material,0),
	coalesce(cd_grupo_material,0),
	coalesce(cd_estabelecimento,0);


BEGIN

ie_agrupar_w	:= 'S';

begin
select	1
into STRICT	qt_regra_w
from	regra_agrup_mat_convenio
where	coalesce(cd_estabelecimento,coalesce(cd_estabelecimento_p,0))	= coalesce(cd_estabelecimento_p,0)  LIMIT 1;
exception
when others then
	qt_regra_w	:= 0;
end;

if (qt_regra_w = 1) then

	begin
	select	a.dt_conta,
		a.cd_material,
		b.cd_grupo_material,
		b.cd_subgrupo_material,
		b.cd_classe_material
	into STRICT	dt_material_w,
		cd_material_w,
		cd_grupo_material_w,
		cd_subgrupo_material_w,
		cd_classe_material_w
	from	material_atend_paciente a,
		estrutura_material_v b
	where	a.cd_material	= b.cd_material
	and	a.nr_sequencia	= nr_seq_matpaci_p  LIMIT 1;
	exception
	when others then
		qt_regra_w	:= 0;
	end;

	open C01;
	loop
	fetch C01 into
		ie_agrupar_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		ie_agrupar_w	:= ie_agrupar_w;
	end loop;
	close C01;

end if;

return	ie_agrupar_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
