SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION ish_get_sapevent ( nr_seq_hcm_patient_p text) RETURNS varchar AS $body$
DECLARE


	nr_seq_hcm_seg_pat_w	hcm_patienten.nr_seq_segmento%type;
	nr_seq_mensagem_w	hcm_segmento.nr_seq_mensagem%type;
	nr_seq_hcm_seg_hea_w	hcm_kopf.nr_seq_segmento%type;
	sapevent_w		hcm_kopf.sapevent%type;

	
BEGIN
	
	begin
	select	nr_seq_segmento
	into STRICT	nr_seq_hcm_seg_pat_w
	from	hcm_patienten
	where	nr_sequencia = nr_seq_hcm_patient_p;

	select	nr_seq_mensagem
	into STRICT	nr_seq_mensagem_w
	from	hcm_segmento
	where	nr_sequencia = nr_seq_hcm_seg_pat_w;

	select	nr_sequencia
	into STRICT	nr_seq_hcm_seg_hea_w
	from	hcm_segmento
	where	nr_seq_mensagem = nr_seq_mensagem_w
	and	ie_tipo = 'HEA'  LIMIT 1;

	select	sapevent
	into STRICT	sapevent_w
	from	hcm_kopf
	where	nr_seq_segmento = nr_seq_hcm_seg_hea_w;
	exception
	when others then
		sapevent_w	:= null;
	end;
	
	return	sapevent_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION ish_get_sapevent ( nr_seq_hcm_patient_p text) FROM PUBLIC;

