CREATE OR REPLACE FUNCTION ish_obter_se_reg_alterado ( nr_seq_fila_p bigint, creation_user_p text, update_user_p text, cancel_user_p text default null) RETURNS varchar AS $body$
DECLARE
	-- Change this to reflect the dblink connection string
	v_conn_str  text := format('port=%s dbname=%s user=%s', current_setting('port'), current_database(), current_user);
	v_query     text;

	v_ret	varchar;
