CREATE OR REPLACE FUNCTION is_atendimento_acompanhante ( nr_atendimento_p atendimento_paciente.nr_atendimento%type) RETURNS TIPO_EPISODIO.IE_ACOMPANHANTE_PACIENTE%TYPE AS $body$
DECLARE


ie_acompanhante_w               tipo_episodio.ie_acompanhante_paciente%type;


BEGIN

select  coalesce(max(b.ie_acompanhante_paciente),'N')
into STRICT    ie_acompanhante_w
from    episodio_paciente a
		left join tipo_episodio b
		on a.nr_seq_tipo_episodio = b.nr_sequencia
where   a.nr_sequencia = obter_episodio_atendimento(nr_atendimento_p);

return  ie_acompanhante_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 IMMUTABLE;
