CREATE OR REPLACE FUNCTION is_attribute_fk ( nm_table_p text, nm_attribute_p text) RETURNS varchar AS $body$
DECLARE


ie_fk_w		varchar(1);


BEGIN

select	CASE WHEN count(*)=0 THEN  'N'  ELSE 'Y' END
into STRICT	ie_fk_w
from	tabela_atributo a,
	integridade_referencial b,
	integridade_atributo c
where	b.nm_tabela = a.nm_tabela
and	b.nm_tabela = c.nm_tabela
and	b.nm_integridade_referencial = c.nm_integridade_referencial
and	c.nm_atributo = a.nm_atributo
and	a.nm_tabela = nm_table_p
and	a.nm_atributo = nm_attribute_p;

return	ie_fk_w;

end;
$body$
