CREATE OR REPLACE FUNCTION is_attribute_pk ( nm_table_p text, nm_attribute_p text) RETURNS varchar AS $body$
DECLARE


ie_pk_w		varchar(1);


BEGIN

select	CASE WHEN count(*)=0 THEN  'N'  ELSE 'Y' END
into STRICT	ie_pk_w
from	tabela_atributo a,
	indice c,
	indice_atributo d
where	c.nm_tabela = d.nm_tabela
and	c.nm_indice = d.nm_indice
and	c.nm_tabela = a.nm_tabela
and	d.nm_atributo = a.nm_atributo
and	c.ie_tipo = 'PK'
and	a.nm_tabela = nm_table_p
and	a.nm_atributo = nm_attribute_p;

return	ie_pk_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
