CREATE OR REPLACE FUNCTION is_cp_team_active_atx (nr_sequencia_p protocolo_integrado_time.nr_sequencia%type) RETURNS varchar AS $body$
DECLARE
is_active_w                protocolo_integrado_time.ie_situacao%type;
ie_situaco_atual_w         protocolo_integrado_time.ie_situacao%type;


BEGIN

select coalesce(max('A'), 'I')
    into STRICT is_active_w
from protocolo_integrado_time
    where nr_sequencia = nr_sequencia_p
    and dt_liberacao <= clock_timestamp()
    and (dt_validade >= clock_timestamp() or coalesce(dt_validade::text, '') = '');

select max(ie_situacao)
    into STRICT ie_situaco_atual_w
from protocolo_integrado_time
    where nr_sequencia = nr_sequencia_p;

if (is_active_w != ie_situaco_atual_w) then
        update protocolo_integrado_time set IE_SITUACAO = is_active_w where nr_sequencia = nr_sequencia_p;
        commit;
end if;	

return is_active_w;
			
end;
$body$
LANGUAGE PLPGSQL
