CREATE OR REPLACE FUNCTION is_discharge_step_enabled ( ie_discharge_level_p text, nm_user_p text, cd_profile_p bigint) RETURNS varchar AS $body$
DECLARE


ie_step_allowed_w	varchar(1);

C01 CURSOR FOR
	SELECT	coalesce(ie_permite, 'N') ie_allowed
	from	permissao_etapa_alta
	where	((coalesce(ie_etapa_alta::text, '') = '') or (ie_etapa_alta = ie_discharge_level_p))
	and	((coalesce(cd_perfil::text, '') = '') or (cd_perfil = cd_profile_p))
	and	((coalesce(nm_usuario::text, '') = '') or (nm_usuario = nm_user_p))
	order by ie_etapa_alta, nm_usuario, cd_perfil;


BEGIN
-- If there's no rule defined for this level, the user is allowed to do it
ie_step_allowed_w := 'S';

open C01;
loop
fetch C01 into
	ie_step_allowed_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	exit;
end loop;
close C01;

return	ie_step_allowed_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
