CREATE OR REPLACE FUNCTION is_eps_convenio ( CD_CONVENIO_P CONVENIO_PLANO.CD_CONVENIO%TYPE, CD_PLANO_P CONVENIO_PLANO.CD_PLANO%TYPE ) RETURNS varchar AS $body$
DECLARE

  DS_RESULT_W	varchar(1);

BEGIN
  SELECT  coalesce(MAX(CP.IE_EPS), 'N')
  INTO STRICT    DS_RESULT_W
  FROM    CONVENIO_PLANO CP
  WHERE   CP.CD_CONVENIO = CD_CONVENIO_P
  AND     CP.CD_PLANO = CD_PLANO_P
  AND     coalesce(CP.IE_EPS, 'N') = 'S';

  RETURN DS_RESULT_W;
EXCEPTION
  WHEN no_data_found THEN RETURN 'N';
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
