CREATE OR REPLACE FUNCTION is_external_physician_valid ( cd_pessoa_fisica_paciente_p bigint, cd_medico_p bigint) RETURNS varchar AS $body$
DECLARE


return_w varchar(1);


BEGIN

select coalesce(max('S'), 'N')
into STRICT return_w
from PF_MEDICO_EXTERNO pme
where pme.cd_pessoa_fisica = cd_pessoa_fisica_paciente_p 
and pme.cd_medico = cd_medico_p 
and (((pme.DT_FIM_VIGENCIA IS NOT NULL AND pme.DT_FIM_VIGENCIA::text <> '') and (pme.DT_INICIO_VIGENCIA IS NOT NULL AND pme.DT_INICIO_VIGENCIA::text <> '') and clock_timestamp() BETWEEN pme.DT_INICIO_VIGENCIA and pme.DT_FIM_VIGENCIA)
or (coalesce(pme.DT_FIM_VIGENCIA::text, '') = '' and (pme.DT_INICIO_VIGENCIA IS NOT NULL AND pme.DT_INICIO_VIGENCIA::text <> '') and clock_timestamp() BETWEEN pme.DT_INICIO_VIGENCIA and clock_timestamp() + interval '1 days')
or ((pme.DT_FIM_VIGENCIA IS NOT NULL AND pme.DT_FIM_VIGENCIA::text <> '') and coalesce(pme.DT_INICIO_VIGENCIA::text, '') = '' and clock_timestamp() BETWEEN clock_timestamp() - interval '1 days' and pme.DT_FIM_VIGENCIA)
or (coalesce(pme.DT_FIM_VIGENCIA::text, '') = '' and coalesce(pme.DT_INICIO_VIGENCIA::text, '') = ''));

return	return_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
