CREATE OR REPLACE FUNCTION is_first_encounter_atx ( nr_atendimento_p bigint, nr_seq_episodio_p bigint) RETURNS varchar AS $body$
DECLARE


is_first_encounter_w	varchar(1)	:= 'N';
nr_atendimento_w	bigint;
BEGIN

select	min(nr_atendimento)
into STRICT	nr_atendimento_w
from	atendimento_paciente
where	coalesce(dt_cancelamento::text, '') = ''
and	nr_seq_episodio	= nr_seq_episodio_p;

if (nr_atendimento_w = nr_atendimento_p) then
	is_first_encounter_w	:= 'S';
end if;

return	is_first_encounter_w;

end;
