CREATE OR REPLACE FUNCTION is_hist_so_in_sla ( nr_seq_ordem_servico_p text, nr_seq_tipo_hist_p man_ordem_serv_tecnico.nr_seq_tipo%type ) RETURNS varchar AS $body$
DECLARE

/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: Retorna 'S' caso a ordem de servico possua regra de SLA
-------------------------------------------------------------------------------------------------------------------

Locais de chamada direta: 
[  ]  Objetos do dicionário [ X ] Tasy (Delphi/Java/HTML5) [  ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------

Pontos de atenção:
-------------------------------------------------------------------------------------------------------------------

Referências:
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
ds_retorno_w		varchar(1);

nr_seq_localizacao_w	man_localizacao.nr_sequencia%type;

dt_ordem_servico_w	man_ordem_servico.dt_ordem_servico%type;
ie_classificacao_w	man_ordem_servico.ie_classificacao%type;
ie_prioridade_w		man_ordem_servico.ie_prioridade%type;


BEGIN

	select	coalesce(max('S'), 'N')
	into STRICT	ds_retorno_w
	from	man_sla ms,
		man_sla_regra msr,
		man_sla_loc msl,
		man_ordem_servico mos
	where	ms.nr_sequencia = msr.nr_seq_sla
	and	ms.nr_sequencia = msl.nr_seq_sla
	and	mos.dt_ordem_servico between ms.dt_vigencia and coalesce(ms.dt_fim_vigencia, clock_timestamp())
	and	msr.ie_classificacao = mos.ie_classificacao
	and	msr.ie_prioridade = mos.ie_prioridade
	and	msl.nr_seq_local = mos.nr_seq_localizacao
	and	msr.nr_seq_tipo_hist = nr_seq_tipo_hist_p
	and	(msr.nr_seq_tipo_hist IS NOT NULL AND msr.nr_seq_tipo_hist::text <> '')
	and	mos.nr_sequencia = nr_seq_ordem_servico_p;
	
	return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
