CREATE OR REPLACE FUNCTION is_special_order_rule ( order_type_p text, integration_type_p text, cd_estabelecimento_p bigint ) RETURNS varchar AS $body$
DECLARE


/**
Integration_Type_P -
D - Special order transmit to Department (CareStream , ITech)
A - Special order transmit to Accounting system (NAIS)
**/
ie_reterno_w varchar(1);


BEGIN

    select CASE WHEN count(*)=0 THEN  'N'  ELSE 'S' END
    into STRICT    ie_reterno_w
    from   transmissao_ordem_especial
    where ie_order_type = order_type_p
        and coalesce(ie_situacao, 'I') = 'A'
        and coalesce(cd_estabelecimento, wheb_usuario_pck.get_cd_estabelecimento) = cd_estabelecimento_p
        and (( coalesce(ie_transmit_department, 'N') = 'Y' and integration_type_p = 'D') or (coalesce(ie_transmit_account, 'N') = 'Y' and integration_type_p = 'A'));

    return ie_reterno_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
