CREATE OR REPLACE FUNCTION is_system_integration_eclipse () RETURNS varchar AS $body$
DECLARE

  result_w varchar(1);
  cd_integration_system_w eclipse_parameters.cd_integration_system%type;

BEGIN
  SELECT coalesce(cd_integration_system, 'N')
  INTO STRICT cd_integration_system_w
  FROM (SELECT cd_integration_system
    FROM eclipse_parameters
    WHERE cd_estabelecimento = obter_estabelecimento_ativo()
    ORDER BY dt_atualizacao DESC
