CREATE OR REPLACE FUNCTION item_ordem_dose_range (nr_seq_mat_cpoe_p text) RETURNS varchar AS $body$
DECLARE


ie_dose_range_w	varchar(1);


BEGIN

  if (nr_seq_mat_cpoe_p IS NOT NULL AND nr_seq_mat_cpoe_p::text <> '') then
    begin
    select ie_dose_range
    into STRICT	ie_dose_range_w
    from	cpoe_material
    where	nr_sequencia = nr_seq_mat_cpoe_p;
    end;
  end if;

  if (coalesce(ie_dose_range_w::text, '') = '' or ie_dose_range_w = '') then
    ie_dose_range_w := 'N';
  end if;

  return ie_dose_range_w;

end;
