CREATE OR REPLACE FUNCTION km_obter_result_avaliacao (nr_seq_avaliacao_p km_avaliacao.nr_sequencia%type) RETURNS bigint AS $body$
DECLARE


pr_conhecimento_w	double precision;


BEGIN

select	coalesce(sum(a.vl_resposta * b.qt_peso_item) / sum(b.qt_peso_item) / 4 * 100,0) pr_conhecimento
into STRICT	pr_conhecimento_w
from	km_questionario_item b,
	km_avaliacao_item a
where	b.nr_sequencia = a.nr_seq_item_quest
and	a.nr_seq_avaliacao = nr_seq_avaliacao_P;

return pr_conhecimento_w;

end;
