CREATE OR REPLACE FUNCTION km_obter_se_quest_nao_resp ( cd_funcao_pp funcao.cd_funcao%type default null, nr_seq_habilidade_p km_habilidade_tecnica.nr_sequencia%type default null, cd_setor_atendimento_p setor_atendimento.cd_setor_atendimento%type DEFAULT NULL, nm_usuario_p usuario.nm_usuario%type DEFAULT NULL) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(1);


BEGIN

select	coalesce(max('S'), 'N') ie_existe
into STRICT	ds_retorno_w
from	km_avaliacao a,
	km_questionario q
where	q.nr_sequencia = a.nr_seq_questionario
and	coalesce(q.cd_funcao,0) = coalesce(cd_funcao_pp,0)
and	coalesce(q.nr_seq_habilidade,0) = coalesce(nr_seq_habilidade_p,0)
and	q.ie_situacao = 'A'
and	coalesce(a.dt_liberacao::text, '') = ''
and	a.nm_usuario = nm_usuario_p
and	exists (SELECT	1
		from	km_questionario_lib ql
		where	ql.nr_seq_questionario = q.nr_sequencia
		and	ql.cd_setor_atendimento = cd_setor_atendimento_p);

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
