SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION l10nger_converte_protpy ( ds_conteudo_p text) RETURNS varchar AS $body$
DECLARE


C01 CURSOR FOR
SELECT	ds_protpy,
	ds_char
from	l10nger_protpy_v
where	ds_conteudo_p like '%' || ds_protpy || '%';

c01_w	C01%rowtype;

ds_retorno_w	varchar(4000);


BEGIN
ds_retorno_w	:=	ds_conteudo_p;

open C01;
loop
fetch C01 into
	c01_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	ds_retorno_w	:=	replace(ds_retorno_w, c01_w.ds_protpy, c01_w.ds_char);
	end;
end loop;
close C01;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
