SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION labweb_conta_paga_regra (nr_atendimento_p text, nr_prescricao_p bigint DEFAULT null, nr_seq_prescr_p bigint default null) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(1);



BEGIN

	if (nr_prescricao_p is  null
        or coalesce(nr_seq_prescr_p::text, '') = '')then
		select 	CASE WHEN count(*)='0' THEN 'N'  ELSE 'S' END 
		into STRICT	ds_retorno_w
		from 	conta_paciente a
		where	a.nr_atendimento = nr_atendimento_p
		and (a.ie_status_acerto = 2 and coalesce(nr_seq_protocolo,0) <> 0);

		if (ds_retorno_w = 'N') then

			select 	CASE WHEN count(*)='0' THEN 'N'  ELSE 'S' END
			into STRICT	ds_retorno_w
			from 	labweb_conv_cobranca a,
				conta_paciente b
			where 	coalesce(a.ie_situacao,'A') = 'A'
			and	a.cd_convenio = b.cd_convenio_parametro
			and	a.cd_convenio = Obter_Convenio_Atendimento(nr_atendimento_p);

		end if;
	else
		select 	CASE WHEN count(*)='0' THEN 'N'  ELSE 'S' END 
		into STRICT	ds_retorno_w
		from 	conta_paciente a,
				procedimento_paciente b
		where	a.nr_atendimento 			= nr_atendimento_p
		and 	a.ie_status_acerto 			= 2 
		and 	a.nr_interno_conta 			= b.nr_interno_conta
		and		b.nr_prescricao 			= nr_prescricao_p
		and		b.nr_sequencia_prescricao	= nr_seq_prescr_p;

	end if;

	return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
