CREATE OR REPLACE FUNCTION labweb_obter_desc_status ( nr_prescricao_p bigint, nr_seq_prescricao_p bigint, ie_param_status_p bigint, ds_desc_status_p text, ds_mensagem_proc_p text, ds_mensagem_bloq_p text, nm_usuario_p text, cd_estabelecimento_p bigint) RETURNS varchar AS $body$
DECLARE

 
ds_retorno_w		varchar(255);
ie_status_atend_w	smallint;


BEGIN 
 
ds_retorno_w := '';
 
if (ie_param_status_p > 0 ) then 
	if (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') and (nr_seq_prescricao_p IS NOT NULL AND nr_seq_prescricao_p::text <> '') then 
		select 	MAX(ie_status_atend) 
		into STRICT	ie_status_atend_w 
		from	prescr_procedimento 
		where 	nr_prescricao = nr_prescricao_p 
		and		nr_sequencia = nr_seq_prescricao_p;
 
		if (ie_status_atend_w <= ie_param_status_p) and (obter_status_result_exame_site(nr_prescricao_p,nr_seq_prescricao_p,nm_usuario_p,cd_estabelecimento_p) <> 'L')then 
			ds_retorno_w := ds_desc_status_p;
		elsif (obter_status_result_exame_site(nr_prescricao_p,nr_seq_prescricao_p,nm_usuario_p,cd_estabelecimento_p) = 'L') or (ie_status_atend_w > ie_param_status_p)then 
			  select CASE WHEN obter_status_result_exame_site(nr_prescricao_p,nr_seq_prescricao_p,nm_usuario_p,cd_estabelecimento_p)='P' THEN  ds_mensagem_proc_p WHEN obter_status_result_exame_site(nr_prescricao_p,nr_seq_prescricao_p,nm_usuario_p,cd_estabelecimento_p)='B' THEN  ds_mensagem_bloq_p  ELSE CASE WHEN Obter_Regra_Status_Parcial(nr_prescricao_p, nr_seq_prescricao_p)=' ' THEN  obter_desc_expressao(292549)  ELSE Obter_Regra_Status_Parcial(nr_prescricao_p, nr_seq_prescricao_p) END  END  
			  into STRICT	ds_retorno_w 
			;
		end if;
	end if;
else 
	select CASE WHEN obter_status_result_exame_site(nr_prescricao_p,nr_seq_prescricao_p,nm_usuario_p,cd_estabelecimento_p)='P' THEN  ds_mensagem_proc_p WHEN obter_status_result_exame_site(nr_prescricao_p,nr_seq_prescricao_p,nm_usuario_p,cd_estabelecimento_p)='B' THEN  ds_mensagem_bloq_p  ELSE CASE WHEN Obter_Regra_Status_Parcial(nr_prescricao_p, nr_seq_prescricao_p)=' ' THEN  obter_desc_expressao(292549)  ELSE Obter_Regra_Status_Parcial(nr_prescricao_p, nr_seq_prescricao_p) END  END  
  into STRICT	ds_retorno_w 
;
end if;
 
 
return	ds_retorno_w;
 
end;
