CREATE OR REPLACE FUNCTION lab_cd_barras_amostra (nr_prescricao_p bigint, nr_seq_prescr_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(80);
ie_padrao_amostra_w lab_parametro.ie_padrao_amostra%type;


BEGIN


if (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') and (nr_seq_prescr_p IS NOT NULL AND nr_seq_prescr_p::text <> '') then

	select  MAX(a.ie_padrao_amostra)
	into STRICT	ie_padrao_amostra_w
	from	lab_parametro a,
		prescr_medica b
	where	a.cd_estabelecimento = b.cd_estabelecimento
	and	b.nr_prescricao = nr_prescricao_p;

	select  MAX(lab_obter_barras_amostra(a.nr_prescricao,a.nr_sequencia,b.nr_sequencia,ie_padrao_amostra_w))
	into STRICT    ds_retorno_w
	from	prescr_procedimento a,
		prescr_proc_material b,
		material_exame_lab c
	where 	a.nr_prescricao = b.nr_prescricao
	and	a.cd_material_exame = c.cd_material_exame
	and	b.nr_seq_material = c.nr_sequencia
	and	(a.nr_seq_exame IS NOT NULL AND a.nr_seq_exame::text <> '')
	and	a.nr_prescricao = nr_prescricao_p
	and	a.nr_sequencia = nr_seq_prescr_p;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
