SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION lab_cons_valor_result_lote (nr_seq_exame_p bigint, nr_seq_material_p bigint, qt_result_p bigint, ds_result_p text, nr_prescricao_p bigint, nr_seq_prescr_p bigint, ie_opcao_p bigint, nr_seq_metodo_p exame_lab_result_item.nr_seq_metodo%type default null, nr_seq_resultado_p exame_lab_result_item.nr_seq_resultado%type default null) RETURNS varchar AS $body$
DECLARE


ie_tipo_busca_w             varchar(1);
ie_consiste_w               varchar(1);
ds_resultado_sugerido_w     varchar(255);
ds_material_criterio_w      varchar(255);
ds_metodo_criterio_w        varchar(255);
qt_dias_prev_w              varchar(255);
ds_retorno_w                varchar(4000);
ds_acao_criterio_w          varchar(4000);

qt_minima_w                 bigint;
qt_maxima_w                 bigint;
pr_minimo_w                 bigint;
pr_maximo_w                 bigint;
nr_sequencia_padrao_w       bigint;
nr_sequencia_criterio_w     bigint;
ie_tipo_valor_w             bigint := 0;

ds_referencia_w             exame_lab_padrao.ds_observacao%type;
nr_seq_metodo_w             exame_lab_result_item.nr_seq_metodo%type;
nr_seq_resultado_w          exame_lab_result_item.nr_seq_resultado%type;
ie_formato_resultado_w      exame_laboratorio.ie_formato_resultado%type;
ds_mensagem_criterio_w      lab_valor_padrao_criterio.ds_mensagem%type;
ie_acao_criterio_lote_w     lab_valor_padrao_criterio.ie_acao_criterio%type;
ie_acao_criterio_w          lote_ent_patol_exam_crit.ie_acao_criterio%type;

--pragma autonomous_transaction;
BEGIN

/*
ie_opcao_p
1  - Retorna descricao do tipo de valor
2  - Retorna o ie_tipo_valor como number
3  - Retorna a mensagem do criterio
4  - Retorna a acao cadastrada no criterio
5  - Retorna o QT_DIAS cadastrado para o criterio
6  - Retorna o TIPO DE BUSCA cadastrado para o criterio
7  - Retorna codigo IE_ACAO_CRITERIO
8  - Retorna resultado sugerido
9  - Retorna o material do criterio
10 - Retorna o metodo do criterio
11 - Retorna codigo IE_ACAO_CRITERIO para aceitavel e nao aceitavel
*/
if (coalesce(nr_seq_resultado_p::text, '') = '') then
    select  b.nr_seq_metodo,
            b.nr_seq_resultado
    into STRICT    nr_seq_metodo_w,
            nr_seq_resultado_w
    from    exame_lab_resultado a inner join
            exame_lab_result_item b ON (a.nr_seq_resultado = b.nr_seq_resultado)
    where   a.nr_prescricao = nr_prescricao_p and
            b.nr_seq_exame  = nr_seq_exame_p and
            b.nr_seq_prescr = nr_seq_prescr_p;
else
    select  nr_seq_metodo_p,
            nr_seq_resultado_p
    into STRICT    nr_seq_metodo_w,
            nr_seq_resultado_w
;
end if;

select  max(ie_formato_resultado)
into STRICT    ie_formato_resultado_w
from    exame_laboratorio
where   nr_seq_exame = nr_seq_exame_p;

SELECT * FROM define_reference_result_values( nr_prescricao_p, nr_seq_prescr_p, nr_seq_resultado_w, qt_result_p, qt_result_p, ds_result_p, nr_seq_exame_p, nr_seq_material_p, nr_seq_metodo_w, qt_minima_w, qt_maxima_w, pr_minimo_w, pr_maximo_w, ds_referencia_w, ie_consiste_w, nr_sequencia_padrao_w, ds_mensagem_criterio_w, ie_acao_criterio_lote_w, nr_sequencia_criterio_w) INTO STRICT qt_minima_w, qt_maxima_w, pr_minimo_w, pr_maximo_w, ds_referencia_w, ie_consiste_w, nr_sequencia_padrao_w, ds_mensagem_criterio_w, ie_acao_criterio_lote_w, nr_sequencia_criterio_w;

if ((ie_formato_resultado_w = 'P') or (ie_formato_resultado_w = 'V') or (ie_formato_resultado_w = 'D') or (ie_formato_resultado_w = 'DV')) then
    begin
    select  ie_tipo_valor,
            CASE WHEN ie_acao_criterio='T' THEN WHEB_MENSAGEM_PCK.get_texto(312176) WHEN ie_acao_criterio='R' THEN WHEB_MENSAGEM_PCK.get_texto(312177) WHEN ie_acao_criterio='N' THEN WHEB_MENSAGEM_PCK.get_texto(312178) WHEN ie_acao_criterio='D' THEN WHEB_MENSAGEM_PCK.get_texto(312179) END ,
            qt_dias_prev,
            ie_gerar_busca_ativa,
            ie_acao_criterio,
            ds_result_sugerido,
            nr_seq_material,
            nr_seq_metodo
    into STRICT    ie_tipo_valor_w,
            ds_acao_criterio_w,
            qt_dias_prev_w,
            ie_tipo_busca_w,
            ie_acao_criterio_w,
            ds_resultado_sugerido_w,
            ds_material_criterio_w,
            ds_metodo_criterio_w
    from (SELECT a.ie_tipo_valor,
                    a.nr_sequencia,
                    b.ds_mensagem,
                    b.ie_acao_criterio,
                    b.qt_dias_prev,
                    coalesce(b.ie_gerar_busca_ativa,'A') ie_gerar_busca_ativa,
                    b.ds_result_sugerido,
                    b.nr_seq_material,
                    b.nr_seq_metodo
            from    exame_lab_padrao a,
                    lab_valor_padrao_criterio b
            where   a.nr_sequencia = nr_sequencia_padrao_w and
                    b.nr_sequencia = nr_sequencia_criterio_w and
                    a.nr_seq_exame = nr_seq_exame_p) alias10 LIMIT 1;
    exception
        when no_data_found then
            ie_tipo_valor_w := 0;
    end;
end if;

if (ie_formato_resultado_w = 'DV') then
    if (ie_opcao_p = 1) then
        if (ie_tipo_valor_w = 0) then
            ds_retorno_w := Wheb_mensagem_pck.get_texto(308858); --'Referancia';
        elsif (ie_tipo_valor_w = 1) then
            ds_retorno_w := Wheb_mensagem_pck.get_texto(308859); --'Aceitavel';
        elsif (ie_tipo_valor_w = 2) then
            ds_retorno_w := Wheb_mensagem_pck.get_texto(308860); --'Nao aceitavel';
        end if;
    elsif (ie_opcao_p = 2) then
        if (ie_tipo_valor_w = 0) then
            ds_retorno_w := '0';
        elsif (ie_tipo_valor_w = 1) then
            ds_retorno_w := '1';
        elsif (ie_tipo_valor_w = 2) then
            ds_retorno_w := '2';
        end if;
    end if;
end if;

if (ie_tipo_valor_w = 0) and (coalesce(nr_sequencia_padrao_w,0) <> 0) then
    if (ie_opcao_p = 1) then
        ds_retorno_w := Wheb_mensagem_pck.get_texto(308858); --'Referencia';
    elsif (ie_opcao_p = 2) then
        ds_retorno_w := '0';
    end if;
elsif ((ie_tipo_valor_w = 1) or (ie_tipo_valor_w = 2)) then
    if (ie_opcao_p = 1) then
        if (ie_tipo_valor_w = 1) then
            ds_retorno_w := Wheb_mensagem_pck.get_texto(308857); --'Abaixo/Acima da referencia';
        else
            ds_retorno_w := Wheb_mensagem_pck.get_texto(308856); --'Muito abaixo/acima da referencia';
        end if;
    elsif (ie_opcao_p = 2) then
        if (ie_tipo_valor_w = 1) then
            ds_retorno_w := '1';
        else
            ds_retorno_w := '2';
        end if;
    elsif (ie_opcao_p = 3) then
        ds_retorno_w := ds_mensagem_criterio_w;
    elsif (ie_opcao_p = 4) then
        ds_retorno_w := ds_acao_criterio_w;
    elsif (ie_opcao_p = 5) then
        ds_retorno_w := qt_dias_prev_w;
    elsif (ie_opcao_p = 6) then
        ds_retorno_w := ie_tipo_busca_w;
    elsif (ie_opcao_p = 7) then
        ds_retorno_w := ie_acao_criterio_w;
    elsif (ie_opcao_p = 8) then
        ds_retorno_w := ds_resultado_sugerido_w;
    elsif (ie_opcao_p = 9) then
        ds_retorno_w := ds_material_criterio_w;
    elsif (ie_opcao_p = 10) then
        ds_retorno_w := ds_metodo_criterio_w;
    end if;
end if;

if (ie_opcao_p = 11) then
    if (coalesce(ie_acao_criterio_w,'S') = 'S') then
        ds_retorno_w := '1';
    else
        ds_retorno_w := '2';
    end if;
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION lab_cons_valor_result_lote (nr_seq_exame_p bigint, nr_seq_material_p bigint, qt_result_p bigint, ds_result_p text, nr_prescricao_p bigint, nr_seq_prescr_p bigint, ie_opcao_p bigint, nr_seq_metodo_p exame_lab_result_item.nr_seq_metodo%type default null, nr_seq_resultado_p exame_lab_result_item.nr_seq_resultado%type default null) FROM PUBLIC;

