CREATE OR REPLACE FUNCTION lab_dif_aprov_infor_crit (nr_prescricao_p text, dt_aprovacao_p timestamp) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(255);
dt_contato_medico_w	prescr_contato_result_crit.dt_contato%type;


BEGIN

select 	Max(dt_contato)
into STRICT	dt_contato_medico_w
from	prescr_contato_result_crit
where	nr_prescricao = nr_prescricao_p;

select  dt_contato_medico_w - dt_aprovacao_p
into STRICT	ds_retorno_w
;

if (ds_retorno_w < 0) then
	ds_retorno_w := 0;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
