CREATE OR REPLACE FUNCTION lab_existe_exame_aviso_sonoro (nr_prescricao_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w  		varchar(1) := 'N';
ds_param_w		varchar(255);


BEGIN
	ds_param_w := obter_param_usuario(722, 353, wheb_usuario_pck.get_cd_perfil, wheb_usuario_pck.get_nm_usuario, wheb_usuario_pck.get_cd_estabelecimento, ds_param_w);
	
	if (nr_prescricao_p > 0) then
			
		SELECT  CASE WHEN COUNT(*)=0 THEN  'N'  ELSE 'S' END
		INTO STRICT  	ds_retorno_w
		FROM  	prescr_procedimento a,
			exame_laboratorio b
		WHERE  	a.nr_seq_exame = b.nr_seq_exame
		AND  	a.nr_prescricao = nr_prescricao_p
		AND (Obter_Se_Contido_char(a.ie_status_atend, replace(ds_param_w, ' ', '')) = 'S' OR coalesce(ds_param_w::text, '') = '') 
		AND  	b.ie_aviso_sonoro = 'S';
				
	end if;

	return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
