CREATE OR REPLACE FUNCTION lab_formata_resultado_fibria (cd_exame_princ_w text, cd_exame_analito_w text, ds_resultado_w text) RETURNS varchar AS $body$
DECLARE


ds_retorno_result_w			varchar(255);


BEGIN

ds_retorno_result_w := ds_resultado_w;

--Exames que devem ser enviados sem a v√≠rgula
if (cd_exame_princ_w	= 'EBA0003' AND cd_exame_analito_w = '030') or
   (cd_exame_princ_w	= 'EBA0004' AND cd_exame_analito_w = '030') or
   (cd_exame_princ_w	= 'EBA0005' AND cd_exame_analito_w = '030') or
   (cd_exame_princ_w	= 'EBA0008' AND cd_exame_analito_w = '030') then
	ds_retorno_result_w := replace(ds_resultado_w,',','');
end if;

--Consistir casa decimal para o PSA
if (cd_exame_princ_w	= 'ESA0071' AND cd_exame_analito_w = '030') then
	ds_retorno_result_w  := trunc(ds_resultado_w,2);
	if (substr(ds_retorno_result_w,1,1) = ',') then
		ds_retorno_result_w := '0'||ds_retorno_result_w;
	end if;
end if;



return	ds_retorno_result_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
