CREATE OR REPLACE FUNCTION lab_ie_exame_formato_mapa ( nr_seq_exame_p bigint, cd_material_exame_p text, nm_usuario_p text) RETURNS varchar AS $body$
DECLARE

ie_formato_w	varchar(1);

BEGIN
select	CASE WHEN count(nr_seq_formato)=0 THEN  'N'  ELSE 'S' END
into STRICT	ie_formato_w
from	exame_lab_format
where	nr_seq_exame = nr_seq_exame_p
and	nr_seq_material = 	(SELECT	max(nr_sequencia)
				from	material_exame_lab b
				where	b.cd_material_exame = cd_material_exame_p)
and	ie_mapa_laudo = 'M'
and	clock_timestamp() >= coalesce(dt_inicio_vigencia,clock_timestamp())
and 	clock_timestamp() <= coalesce(dt_fim_vigencia,clock_timestamp());

return ie_formato_w;

end;
