CREATE OR REPLACE FUNCTION lab_imp_mapa_regra ( nr_prescricao_p bigint, nr_seq_prescr_p bigint, nr_seq_exame_p bigint, cd_setor_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE

 
ie_imprime_mapa_w	varchar(1) := 'S';
cd_estabelecimento_w	estabelecimento.cd_estabelecimento%type;
nr_atendimento_w	atendimento_paciente.nr_atendimento%type;
ie_tipo_atendimento_w	atendimento_paciente.ie_tipo_atendimento%type;
nr_seq_grupo_exame_w	exame_laboratorio.nr_seq_grupo%type;
					

BEGIN 
 
select 	max(a.nr_atendimento), 
	coalesce(max(a.ie_tipo_atendimento),0), 
	max(b.cd_estabelecimento) 
into STRICT	nr_atendimento_w, 
	ie_tipo_atendimento_w, 
	cd_estabelecimento_w 
from	atendimento_paciente a, 
	prescr_medica b 
where  a.nr_atendimento = b.nr_atendimento 
and	b.nr_prescricao = nr_prescricao_p;
 
select 	coalesce(max(nr_seq_grupo),0) 
into STRICT 	nr_seq_grupo_exame_w 
from	exame_laboratorio 
where	nr_seq_exame = nr_seq_exame_p;
 
select	 coalesce(max('N'),'S') 
into STRICT	 ie_imprime_mapa_w 
from	 lab_regra_cons_mapa_imp 
where	 cd_estabelecimento = cd_estabelecimento_w 
and	 coalesce(nr_seq_exame, nr_seq_exame_p)			= nr_seq_exame_p 
and	 coalesce(nr_seq_grupo, nr_seq_grupo_exame_w)		= nr_seq_grupo_exame_w 
and	 coalesce(ie_tipo_atendimento, ie_tipo_atendimento_w)	= ie_tipo_atendimento_w;
 
return 	ie_imprime_mapa_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
