CREATE OR REPLACE FUNCTION lab_obter_amostra_prescr_int (ds_valor_p text, nr_prescricao_p bigint, nr_seq_exame_p bigint, nr_seq_prescr_p bigint) RETURNS varchar AS $body$
DECLARE


ie_existe_tasylab_w	varchar(1) := 'N';
cd_barras_w		varchar(255);


BEGIN

begin
select	'S'
into STRICT	ie_existe_tasylab_w
from	lab_tasylab_cli_prescr a
where	a.nr_prescricao = nr_prescricao_p;
exception
	when others then
	ie_existe_tasylab_w	:= 'N';
	end;

if (ie_existe_tasylab_w = 'S') then
	select	max(b.cd_barras)
	into STRICT	cd_barras_w
	from  	prescr_proc_mat_item a,
		prescr_proc_material b
	where	a.nr_prescricao = b.nr_prescricao
	and	a.nr_seq_prescr_proc_mat = b.nr_sequencia
	and	a.nr_prescricao = nr_prescricao_p
	and	a.nr_seq_prescr = nr_seq_prescr_p;
end if;

if (coalesce(cd_barras_w::text, '') = '') then
	cd_barras_w	:= Lab_obter_amostra_lablink_v2(ds_valor_p, nr_prescricao_p, nr_seq_exame_p, nr_seq_prescr_p);
end if;

return	cd_barras_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
