SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION lab_obter_aprovador_exame ( nr_prescricao_p bigint ) RETURNS varchar AS $body$
DECLARE

			 
ds_retorno_w			varchar(4000);
nm_usuario_aprovacao_w	varchar(100);
ds_string_separacao_w	varchar(3);

c01 CURSOR FOR 
	SELECT	distinct 
			substr(obter_nome_pf(b.cd_medico_resp), 1, 100) 
	from  exame_lab_result_item b, 
			exame_lab_resultado a 
	where	a.nr_seq_resultado	= b.nr_seq_resultado 
	and		a.nr_prescricao   = nr_prescricao_p 
	and		(b.nm_usuario_aprovacao IS NOT NULL AND b.nm_usuario_aprovacao::text <> '') 
	order by 1;


BEGIN 
 
open c01;
loop 
	fetch c01 into	 
		nm_usuario_aprovacao_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		begin 
		ds_retorno_w			:= ds_retorno_w || ds_string_separacao_w || nm_usuario_aprovacao_w;
		ds_string_separacao_w	:= ' - ';
		end;
	end loop;
close c01;
	 
return substr(wheb_mensagem_pck.get_texto(805161,'DS_RETORNO='||ds_retorno_w),1,2000);
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION lab_obter_aprovador_exame ( nr_prescricao_p bigint ) FROM PUBLIC;

