CREATE OR REPLACE FUNCTION lab_obter_aviso_sonoro_ciencia (nr_prescricao_p bigint, ie_status_atend_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w  varchar(1) := 'N';


BEGIN
if (nr_prescricao_p > 0) then
	SELECT 	CASE WHEN COUNT(*)=0 THEN  'N'  ELSE 'S' END
	into STRICT 	ds_retorno_w
	FROM 	prescr_procedimento a
	WHERE 	a.nr_prescricao = nr_prescricao_p
	AND		a.ie_status_atend = ie_status_atend_p
	AND		coalesce(a.dt_ciencia_laboratorio::text, '') = '';
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
