SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION lab_obter_caracteres_amostra ( cd_barras_p bigint, ie_amostra_p text) RETURNS varchar AS $body$
DECLARE


ds_amostra_w		varchar(20);
cd_estabelecimento_w	prescr_medica.cd_estabelecimento%type;


BEGIN

if (ie_amostra_p = 'AMO9') then
	ds_amostra_w	:= lpad(cd_barras_p,9,'0');
elsif (ie_amostra_p = 'AMO10') or (ie_amostra_p = 'AM10F') then
	ds_amostra_w	:= lpad(cd_barras_p,10,'0');
elsif (ie_amostra_p = 'AMO11') or (ie_amostra_p = 'AM11F') then
	ds_amostra_w	:= lpad(cd_barras_p,11,'0');
elsif (ie_amostra_p = 'AMO13') then
	ds_amostra_w	:= lpad(cd_barras_p,13,'0');
elsif (ie_amostra_p = 'EAM10') then
	select	coalesce(max(a.cd_estabelecimento), 1)
	into STRICT	cd_estabelecimento_w
	from	prescr_medica a,
		prescr_proc_material b
	where	a.nr_prescricao = b.nr_prescricao
	  and 	b.cd_barras = to_char(cd_barras_p);
	ds_amostra_w	:= lpad(substr(cd_estabelecimento_w,1,2),2,0)||lpad(cd_barras_p,10,0);
end if;

return	ds_amostra_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
