CREATE OR REPLACE FUNCTION lab_obter_cd_pf_synapse (cd_pessoa_fisica_p text) RETURNS varchar AS $body$
DECLARE


cd_pessoa_fisica_w		varchar(50);
ie_utiliza_pessoa_alter_w	varchar(1);


BEGIN

if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') then
	begin
		select 	coalesce(MAX(ie_utiliza_pessoa_alternativa),'N')
		into STRICT 	ie_utiliza_pessoa_alter_w
		from 	empresa_integr_dados
		where 	nr_seq_empresa_integr = 31;

		if (ie_utiliza_pessoa_alter_w = 'S') then
			begin
				select 	MAX(cd_sistema_ant)
				into STRICT 	cd_pessoa_fisica_w
				from	pessoa_fisica
				where	cd_pessoa_fisica = cd_pessoa_fisica_p;

				if (coalesce(cd_pessoa_fisica_w::text, '') = '') then
					cd_pessoa_fisica_w := cd_pessoa_fisica_p;
				end if;
			end;
		else
			begin
				cd_pessoa_fisica_w := cd_pessoa_fisica_p;
			end;
		end if;
	end;
end if;

return	cd_pessoa_fisica_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
