CREATE OR REPLACE FUNCTION lab_obter_condicao_amostra (nr_prescricao_p bigint, nr_seq_prescr_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(255);


BEGIN

if (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') and (nr_seq_prescr_p IS NOT NULL AND nr_seq_prescr_p::text <> '') then

	select 	max(b.ds_condicao)
	into STRICT	ds_retorno_w
	from  	prescr_procedimento a,
			prescr_proc_material b,
			prescr_proc_mat_item c
	where 	b.nr_prescricao = a.nr_prescricao
	and	  	a.nr_prescricao = c.nr_prescricao
	and	  	a.nr_sequencia  = c.nr_seq_prescr
	and	  	b.nr_sequencia = c.nr_seq_prescr_proc_mat
	and	  	a.nr_prescricao = nr_prescricao_p
	and	  	a.nr_sequencia = nr_seq_prescr_p;


end if;


return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
