CREATE OR REPLACE FUNCTION lab_obter_convenio_shift ( nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE

ie_tipo_atendimento_w	smallint;
cd_convenio_w		integer;
ds_resultado_w		varchar(20);


BEGIN 
 
select	ie_tipo_atendimento, 
	cd_convenio 
into STRICT	ie_tipo_atendimento_w, 
	cd_convenio_w 
from	atendimento_paciente_v 
where	nr_atendimento = nr_atendimento_p;
 
if (ie_tipo_atendimento_w = 1) then 
	ds_resultado_w := obter_conversao_externa(	'44468569000151', 
							'LABORATORIO_SHIFT_V', 
							'CD_CONVENIO_INTERNO', 
							cd_convenio_w);
else 
	ds_resultado_w := obter_conversao_externa(	'44468569000151', 
							'LABORATORIO_SHIFT_V', 
							'CD_CONVENIO_AMBULATORIAL', 
							cd_convenio_w);
end if;
 
return lpad(ds_resultado_w,4,'0');
 
end;
