CREATE OR REPLACE FUNCTION lab_obter_conversao_externa (cd_cgc_p conversao_meio_externo.cd_cgc%type, nm_tabela_p conversao_meio_externo.nm_tabela%type, nm_atributo_p conversao_meio_externo.nm_atributo%type, cd_interno_p conversao_meio_externo.cd_interno%type) RETURNS varchar AS $body$
DECLARE


	cd_externo_w    conversao_meio_externo.cd_externo%type;


BEGIN

	begin
	select  cd_externo
	into STRICT    cd_externo_w
	from    conversao_meio_externo
	where   ((coalesce(cd_cgc_p,'X') = 'X') or (cd_cgc = cd_cgc_p))
	and     upper(nm_tabela) = upper(nm_tabela_p)
	and     upper(nm_atributo) = upper(nm_atributo_p)
	and     cd_interno  = cd_interno_p
	and     coalesce(ie_envio_receb,'A') in ('E','A');
	exception
		when others then
			cd_externo_w := '';
	end;

	return cd_externo_w;

end;
